    var firebaseConfig = {
        apiKey: "AIzaSyARzNyqPUHJJu1Gevojuu_lUqhXSII-hM0",
        authDomain: "tnt-erp.firebaseapp.com",
       // databaseURL: "https://tnt-erp.firebaseio.com",
        projectId: "tnt-erp",
